//键盘事件
var code_flag=true;
var lottery;
var t;
var status;
$(document).keyup(function(e){
    var ev=e||window.event;
    var code=ev.keyCode;
    if(code==48){
        init();
        $(".img").hide();
        one1();
        lottery=48;
        $(".title").show().find("img").attr("src","img/4_03.png");
    }
    if(code==49){
        init();
        $(".img").hide();
        one1();
        lottery=49;
        $(".title").show().find("img").attr("src","img/1_03.png");
    }
    if(code==50){
        init();
        $(".img").hide();
        one1();
        lottery=50;
        $(".title").show().find("img").attr("src","img/2_03.png");
    }
    if(code==51){
        init1();
        $(".img").hide();
        ten1();
        lottery=51;
        $(".title").show().find("img").attr("src","img/3_03.png");
    }
    if(code==52){
        init1();
        $(".img").hide();
        ten1();
        lottery=52;
        $(".title").show().find("img").attr("src","img/5_03.png");
    }
    if(code==32){
        if(code_flag){
            code_flag=false;
            status=lottery;
            if(status==48||status==49||status==50){
                one();
            }else if(status==51||status==52){
                ten();
            }
            $.ajax({
                url:"",
                data:{},
                success:function(){
                    localStorage.results=[1];
                }
            });
        }else{
            code_flag=true;
            if(status==48||status==49||status==50){
                $(".box2").hide();
                $(".pop").show().find(".box1").show();
            }else if(status==51||status==52){
                $(".box1").hide();
                $(".pop").show().find(".box2").show();
            }
            // $(".result").slideDown();
            clearInterval(t);
        }
    }
    if(code==13){
        $(".pop").hide().find(".box1").hide();
        $(".pop").hide().find(".box2").hide();
        $(".img").show();
        $(".title").hide();
        $(".phone_num").hide();
        $(".counter-container").hide();
    }
});


